-- ----------------------------
-- Table structure for t_article
-- ----------------------------
DROP TABLE IF EXISTS `t_article`;
CREATE TABLE `t_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(50) DEFAULT NULL COMMENT '文章标题',
  `author_id` int(11) DEFAULT NULL COMMENT '作者ID',
  `content` mediumtext COMMENT '文章内容',
  `cid` int(5) NOT NULL DEFAULT '0' COMMENT '标签',
  `create_time` datetime DEFAULT NULL COMMENT '创建时间',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '文章是否审核：0为未审核，1为是,2为不通过',
  `check_num` int(11) DEFAULT '0' COMMENT '点击数',
  `img_src` varchar(200) DEFAULT NULL,
  `edit_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '修改时间',
  `lid` int(11) NOT NULL DEFAULT '1' COMMENT '文章等级，结合用户等级',
  PRIMARY KEY (`id`),
  KEY `t_article_ibfk_1` (`cid`),
  KEY `t_article_ibfk_2` (`author_id`),
  CONSTRAINT `t_article_ibfk_1` FOREIGN KEY (`cid`) REFERENCES `t_category` (`cid`) ON UPDATE CASCADE,
  CONSTRAINT `t_article_ibfk_2` FOREIGN KEY (`author_id`) REFERENCES `t_user` (`uid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=162 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_article
-- ----------------------------
INSERT INTO `t_article` VALUES ('77', '若IPO落空，叮咚买菜还能活多久？', '1', '<p class=\"contentFont\" style=\"text-align: justify;\">叮咚买菜缺钱了。&nbsp;</p><p class=\"contentFont\" style=\"text-align: justify;\">2月18日，叮咚买菜被传最快年内赴美上市，至少募资3亿美元。连续亏损下，依靠上市获得所需资金成了必然选择，毕竟叮咚买菜距离上一次传出融资消息，已经过去了9个月。</p><p class=\"contentFont\" style=\"text-align: justify;\">对于生鲜这条烧钱的赛道来说，此次IPO的背后，是叮咚买菜融资续命的体现。</p><p class=\"contentFont\" style=\"text-align: justify;\">但上市并非解药，甚至作为一家还没有实现盈利的零售类公司，是否能得到海外投资者的认同也未可知，所以赚足眼球之后，叮咚买菜还要寻找自己的“续命良方”。</p><p class=\"contentFont\" style=\"text-align: justify;\"><span>01&nbsp; IPO的叮咚，根基仍未稳</span></p><p class=\"contentFont\" style=\"text-align: justify;\">叮咚买菜成立于2014年，前身是上海壹佰米网络科技推出的“叮咚小区APP”，自从2017年上线叮咚买菜之后，开始了大幅扩张之路。</p><p class=\"contentFont\" style=\"text-align: justify;\">从过往的融资经历来看，相较于每日优鲜、兴盛优选等玩家背后站着腾讯、京东等巨头，叮咚买菜属于近年生鲜领域跑出来的“新人”。</p><p class=\"contentFont\" style=\"text-align: justify;\">2018年，叮咚买菜曾创下一年之内拿下6轮融资的纪录，背后集聚了高榕资本、红杉资本中国、启明创投等知名投资机构。</p><p class=\"contentFont\" style=\"text-align: justify;\">作为生鲜赛道的参与者，叮咚买菜也是极少数没有互联网巨头背景的平台。有业内人士认为，叮咚买菜之所以在过去一年实现了较快的增长，核心在于新城市的不断扩张以及用高营补来持续获客。</p><p class=\"contentFont\" style=\"text-align: justify;\">而显然，通过烧钱、强开新城换来的增长质量堪忧，用“无起送”和高营补换来的所谓用户“薅羊毛”习惯盛行，并不能成为其长期发展的基石。</p><p class=\"contentFont\" style=\"text-align: justify;\">近期，更传出其供应商账期突然延长、无法结款导致合作终止等消息。所以叮咚买菜能否跑出这个历史周期律，是否会在上海以外的区域水土不服，还有待商榷。</p><p class=\"contentFont\" style=\"text-align: justify;\">但从2019年之后，叮咚买菜鲜有融资消息曝出，不难看出，叮咚买菜的盈利难问题已经让投资人越发谨慎。据锌财经援引相关知情人士透露，“资本都开始谨慎了，尤其是这两年融资环境都不好，不像前两年那么激进了，再这么烧钱做补贴，谁都受不了的。”</p><p class=\"contentFont\" style=\"text-align: justify;\">在一级市场失去信心之后，叮咚买菜自然将目光瞄准了二级市场，寻求上市来解决资金链问题。</p><p class=\"contentFont\" style=\"text-align: justify;\">生鲜电商赛道从不缺乏竞争者，作为2017年入场的叮咚买菜，在时机上已经输给了盒马鲜生、每日优鲜等玩家。用户作为互联网企业的核心，得用户者得天下，由于入场较晚，叮咚买菜要想获取用户，就必须以更大的力度和补贴去获客。</p><p class=\"contentFont\" style=\"text-align: justify;\">可以看到，对于如今的叮咚买菜，其根基依然未稳，这时候选择IPO，更多的是要找到一个能继续前进的“续命良方”。</p><p class=\"contentFont\" style=\"text-align: justify;\"><span>02&nbsp; 靠烧钱“拉新”，每单连续亏损</span></p><p class=\"contentFont\" style=\"text-align: justify;\">叮咚买菜在走向全国的过程中，步子迈的大了些。</p><p class=\"contentFont\" style=\"text-align: justify;\">去年4月21日，叮咚买菜宣布正式进军北京市场。彼时，叮咚买菜进行了多类促销活动，包括各种高额优惠券，其获客成本居高不下。此前媒体也在报道中提到，叮咚仅上海一个城市一年的补贴投入就高达数亿，这也是叮咚买菜的一贯打法——通过不停地补贴来获客。</p><p class=\"contentFont\" style=\"text-align: justify;\">牛刀财经了解到，叮咚买菜目前“拉新”手段除了通过投入了大量地推以及骑手到社区及公园周边推广以外，还通过发放“108元新人红包”以及推出各种秒杀活动吸引消费者，配送方面则强调低门槛“0起送费、0配送费”。总结起来为&nbsp;“烧钱‘拉新’、烧钱送货”。</p><p class=\"contentFont\" style=\"text-align: justify;\">长期来看，烧钱买用户对于企业来说是一种负担，一旦停止补贴，用户则会大量流失。尤其是在自身仍在亏损，外部融资长期停滞的情况下，补贴式的拉新无异于饮鸩止渴，最后可能解不了渴，反而让自身在亏损的“毒”中越陷越深。</p><p class=\"contentFont\" style=\"text-align: justify;\">汉森供应链董事长、中国电商物流专家黄刚也曾公开分析叮咚买菜，他认为叮咚买菜缺乏流量和消费场景，没有流量支持和供应链保障的扩张很难盈利，并且亏损会越来越严重。</p><p class=\"contentFont\" style=\"text-align: justify;\"><img src=\"https://imagepphcloud.thepaper.cn/pph/image/117/321/837.png\" data-index=\"0\" data-width=\"1080px\" data-height=\"656px\" style=\"text-align: start;\"></p>', '4', '2021-02-27 12:06:11', '1', '54', 'https://imagepphcloud.thepaper.cn/pph/image/117/321/837.png', '2021-03-10 15:54:30', '1');
