DROP TABLE IF EXISTS `t_comment`;
CREATE TABLE `t_comment` (
  `comid` int(11) NOT NULL AUTO_INCREMENT COMMENT '评论表主键',
  `aid` int(11) NOT NULL COMMENT '被评论的新闻id',
  `uid` int(11) NOT NULL COMMENT '用户评论的id',
  `content` text COMMENT '评论内容',
  `com_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '评论时间',
  `illegal` int(11) NOT NULL DEFAULT '0' COMMENT '查看评论是否非法，0为无1为有',
  PRIMARY KEY (`comid`),
  KEY `aid` (`aid`),
  KEY `uid` (`uid`),
  CONSTRAINT `t_comment_ibfk_1` FOREIGN KEY (`aid`) REFERENCES `t_article` (`id`) ON DELETE CASCADE ON UPDATE CASCADE,
  CONSTRAINT `t_comment_ibfk_2` FOREIGN KEY (`uid`) REFERENCES `t_user` (`uid`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=40 DEFAULT CHARSET=utf8;

