CREATE TABLE `t_level` (
  `lid` int(11) NOT NULL AUTO_INCREMENT,
  `lname` varchar(30) DEFAULT NULL COMMENT '等级名称',
  `level` int(3) DEFAULT NULL COMMENT '等级',
  PRIMARY KEY (`lid`)
) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of t_level
-- ----------------------------
INSERT INTO `t_level` VALUES ('1', '普通用户', '1');
INSERT INTO `t_level` VALUES ('2', '中级用户', '2');
INSERT INTO `t_level` VALUES ('3', '高级用户', '3');

-- ----------------------------
-- Table structure for t_user
-- ----------------------------
DROP TABLE IF EXISTS `t_user`;
CREATE TABLE `t_user` (
  `uid` int(11) NOT NULL AUTO_INCREMENT COMMENT '用户主键',
  `uname` varchar(70) NOT NULL DEFAULT '' COMMENT '用户名',
  `password` varchar(100) NOT NULL DEFAULT '' COMMENT '用户密码',
  `ad_role` int(2) NOT NULL DEFAULT '0' COMMENT '是否管理员',
  `lid` int(3) NOT NULL DEFAULT '1' COMMENT '用户等级,默认等级为1',
  `telephone` varchar(11) DEFAULT '' COMMENT '电话',
  `email` varchar(50) DEFAULT '' COMMENT '邮箱',
  `sex` varchar(6) DEFAULT '' COMMENT '性别',
  `active` int(2) DEFAULT '0' COMMENT '是否激活,0否1是',
  `code` varchar(100) DEFAULT '' COMMENT '存放激活码',
  `head_image` varchar(200) DEFAULT NULL COMMENT '用户头像',
  `create_time` datetime DEFAULT NULL ON UPDATE CURRENT_TIMESTAMP COMMENT '用户创建日期',
  `news_name` varchar(255) DEFAULT '' COMMENT '用户昵称',
  PRIMARY KEY (`uid`),
  KEY `lid` (`lid`),
  CONSTRAINT `t_user_ibfk_1` FOREIGN KEY (`lid`) REFERENCES `t_level` (`lid`) ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8;

